+/* Dig Deeper Block styling */
+.dig-deeper-block {
+  position: relative;
+  overflow: hidden;
+}
+
+.dig-deeper-block::before {
+  content: '';
+  position: absolute;
+  top: 0;
+  left: 0;
+  right: 0;
+  height: 3px;
+  background: linear-gradient(90deg, #3b82f6, #8b5cf6);
+}
+
+.dig-deeper-block:hover {
+  transform: translateY(-2px);
+  box-shadow: 0 10px 25px rgba(59, 130, 246, 0.15);
+  transition: all 0.3s ease;
+}
+
+.dig-deeper-block button:not(:disabled):hover {
+  transform: translateY(-1px);
+  box-shadow: 0 8px 20px rgba(59, 130, 246, 0.25);
+}
+
+/* Depth indicator animation */
+.dig-deeper-block .w-3.h-3 {
+  transition: all 0.3s ease;
+}
+
+.dig-deeper-block .w-3.h-3.bg-blue-500 {
+  animation: pulse 2s infinite;
+}
+
+@keyframes pulse {
+  0%, 100% {
+    opacity: 1;
+  }
+  50% {
+    opacity: 0.7;
+  }
+}

